      void ClearData(){
          labMessage.setText("");
          number.setText("");
          name.setText("");
          locationtxt.setText("");
          name.requestFocus();
          Add.setEnabled(true);
          update.setEnabled(false);
          Archive.setEnabled(false);
          String num_seaport = sp.GetAutoNumber();
            number.setText(num_seaport);
      }
      
        seaPort sp = new seaPort();
        void SetValus(){
            sp.setNumber_seaport( Integer.parseInt(number.getText()));
            sp.setName_seaport(name.getText());
            sp.setLocation(locationtxt.getText());
        }
