    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
          buttonGroup1.add(nameRB);
          buttonGroup1.add(numberRB1);
          buttonGroup1.add(Location);
          name.requestFocus();
          Add.setEnabled(true);
          update.setEnabled(false);
          Archive.setEnabled(false);
        database.UseDatabase.fillJTable("select * from seaport where Active=1", jTable_Display_seaport);
        jTable_Display_seaport.getTableHeader().setReorderingAllowed(false);
        String num_seaport = sp.GetAutoNumber();
            number.setText(num_seaport);
    }//GEN-LAST:event_formWindowOpened

